INSERT INTO `con_duanzi` VALUES (103424, 1, '想吃鸡蛋，去超市买，我问老板：“鸡蛋多少钱？”老板：“看你要多少？买的越多越便宜！”我想了想，说：“给我装到免费为止！”老板：“给我打，打到死为止”', '', NULL, '1', 0, 1, 0, 0, '2016-05-11 18:37:08', 1, 'http://www.budejie.com/new-text/67');
