INSERT INTO `con_duanzi` VALUES (103439, 1, '周末,邻居家上高中的女儿,穿条超短裙在玩风筝.无奈技术不行,风筝挣扎了两下,晃晃悠悠地落在了树杈上.看着她可怜兮兮求助的眼神,我立马找了个梯子搭上：“我扶梯子,你上!”有时人和人之间,多一点点关爱,你会发现,连这粉红色...啊……呸!是蔚蓝的天空,也会格外美丽。', '', NULL, '1,5', 0, 1, 0, 0, '2016-05-12 12:55:05', 0, 'http://www.budejie.com/new-text/57');
