INSERT INTO `con_duanzi` VALUES (103659, 1, '去逛街，一商家在做促销活动，买一送一。我拿起一件商品，问老板价格，<br />我：这个多少钱一个？<br />老板：98块，买一送一<br />我：意思我手里这个98块，另一个是免费送？<br />老板：对，这个98，另一个免费送你<br />我：哦，那你帮我把免费的那个包起来，这边这个我就不要了，谢谢！<br />老板看我拿着东西没给钱就走了，风中凌乱……', '', NULL, '1,5', 0, 1, 0, 0, '2016-05-12 07:13:31', 0, 'http://www.budejie.com/new-text/60');
